const App: React.FC = () => {
  // State variables
  const [originalImageFile, setOriginalImageFile] = useState<File | null>(null);
  const [history, setHistory] = useState<string[]>([]);
  const [currentIndex, setCurrentIndex] = useState<number>(0);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [activeTheme, setActiveTheme] = useState<Theme>('game');
  const [activeCategory, setActiveCategory] = useState<Category>('clothing');
  const [isComparing, setIsComparing] = useState<boolean>(false);
  const imageRef = useRef<HTMLImageElement>(null);

  // Helper to convert data URL back to a File object for the API
  const dataUrlToFile = async (dataUrl: string, fileName: string): Promise<File> => {
    const res = await fetch(dataUrl);
    const blob = await res.blob();
    return new File([blob], fileName, { type: blob.type });
  };

  // Pushes a new image state to the history, clearing any 'redo' states
  const processAndPushHistory = (newImageUrl: string) => {
    const newHistory = history.slice(0, currentIndex + 1);
    newHistory.push(newImageUrl);
    setHistory(newHistory);
    setCurrentIndex(newHistory.length - 1);
  };

  const handleFileSelect = useCallback(async (files: FileList | null) => {
    if (!files || files.length === 0) return;
    const file = files[0];
    const imageUrl = URL.createObjectURL(file);
    setOriginalImageFile(file);
    setHistory([imageUrl]);
    setCurrentIndex(0);
    setError(null);
  }, []);

  // Generic handler for all generative API calls
  const applyGenerativeFunction = useCallback(async (
    prompt: string,
    generator: (file: File, prompt: string) => Promise<string>
  ) => {
    if (!originalImageFile) return;
    setIsLoading(true);
    setError(null);
    try {
      // Use the current image state for the next generation
      const currentImageFile = await dataUrlToFile(history[currentIndex], originalImageFile.name);
      const newImageUrl = await generator(currentImageFile, prompt);
      processAndPushHistory(newImageUrl);
    } catch (e: any) {
      console.error(e);
      setError(e.message || 'An unknown error occurred.');
    } finally {
      setIsLoading(false);
    }
  }, [history, currentIndex, originalImageFile]);

  const handleApplyOutfit = (prompt: string) => applyGenerativeFunction(prompt, generateOutfitImage);
  const handleApplyModification = (prompt: string) => applyGenerativeFunction(prompt, generateAdjustedImage);
  const handleApplyEffect = (prompt: string) => applyGenerativeFunction(prompt, generateFilteredImage);
  const handleApplyEnvironment = (prompt: string) => applyGenerativeFunction(prompt, generateBackgroundImage);
  const handleApplyAction = (prompt: string) => applyGenerativeFunction(prompt, generateActionImage);

  const handleUndo = () => setCurrentIndex(prev => Math.max(0, prev - 1));
  const handleRedo = () => setCurrentIndex(prev => Math.min(history.length - 1, prev + 1));
  const handleHistorySelect = (index: number) => setCurrentIndex(index);
  
  // Resets the application to the start screen
  const handleReset = () => {
    setOriginalImageFile(null);
    setHistory([]);
    setCurrentIndex(0);
    setError(null);
    setIsComparing(false);
  };
  
  // Downloads the current image
  const handleDownload = () => {
    if (history[currentIndex]) {
      const link = document.createElement('a');
      link.href = history[currentIndex];
      link.download = `superlook_edit_${Date.now()}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-[var(--cyber-bg)] text-[var(--cyber-text)] overflow-hidden">
      {originalImageFile && (
         <Header 
          themes={Object.keys(themes) as Theme[]}
          activeTheme={activeTheme}
          onThemeChange={setActiveTheme}
        />
      )}
      <main className="flex-grow flex items-center justify-center min-h-0">
        {!originalImageFile ? (
          <StartScreen onFileSelect={handleFileSelect} />
        ) : (
          <div className="w-full h-full p-4 grid grid-cols-[280px_1fr_380px] gap-4">
            {/* Left Panel */}
            <WardrobeOutfitsPanel historyCount={history.length} currentIndex={currentIndex} onSelectIndex={handleHistorySelect} />

            {/* Center Panel */}
            <div className="flex flex-col items-center justify-center gap-4 h-full min-h-0">
              <div className="relative w-full flex-grow border border-[var(--cyber-border)] bg-black/20 flex items-center justify-center overflow-hidden">
                  {isLoading && (
                    <div className="absolute inset-0 bg-black/50 flex items-center justify-center z-10">
                      <Spinner />
                    </div>
                  )}
                  {error && (
                    <div className="absolute bottom-4 left-4 right-4 bg-[var(--cyber-red)] text-white p-3 border border-white/50 z-20 text-sm">
                      <p className="font-bold">Error:</p>
                      <p>{error}</p>
                    </div>
                  )}
                  <img 
                    ref={imageRef}
                    src={isComparing ? history[0] : history[currentIndex]}
                    alt="Character portrait"
                    className={`transition-opacity duration-300 ${isLoading ? 'opacity-30' : 'opacity-100'} max-w-full max-h-full object-contain`}
                  />
              </div>

              {/* Action Toolbar */}
              <div className="cyber-box flex items-center justify-center gap-2 px-4 py-2 w-full max-w-sm">
                 <button onClick={handleUndo} disabled={isLoading || currentIndex === 0} className="p-2 disabled:opacity-50 text-gray-300 hover:text-white disabled:hover:text-gray-300 transition-colors" aria-label="Undo"><UndoIcon className="w-6 h-6"/></button>
                 <button onClick={handleRedo} disabled={isLoading || currentIndex >= history.length - 1} className="p-2 disabled:opacity-50 text-gray-300 hover:text-white disabled:hover:text-gray-300 transition-colors" aria-label="Redo"><RedoIcon className="w-6 h-6"/></button>
                 
                 <button onMouseDown={() => setIsComparing(true)} onMouseUp={() => setIsComparing(false)} onMouseLeave={() => setIsComparing(false)} disabled={isLoading || history.length <= 1} className="p-2 disabled:opacity-50 text-gray-300 hover:text-white disabled:hover:text-gray-300 transition-colors" aria-label="Compare with original"><EyeIcon className="w-6 h-6"/></button>

                 <div className="w-px h-6 bg-[var(--cyber-border)] mx-2"></div>
                 
                 <button onClick={handleDownload} disabled={isLoading} className="p-2 disabled:opacity-50 text-gray-300 hover:text-white disabled:hover:text-gray-300 transition-colors" aria-label="Download image"><DownloadIcon className="w-6 h-6"/></button>
                 <button onClick={handleReset} disabled={isLoading} className="p-2 disabled:opacity-50 text-gray-300 hover:text-[var(--cyber-red)] disabled:hover:text-gray-300 transition-colors" aria-label="Select new image"><CloseIcon className="w-6 h-6"/></button>
              </div>
            </div>

            {/* Right Panel */}
            <WardrobePanel 
              activeCategory={activeCategory}
              setActiveCategory={setActiveCategory}
              onApplyOutfit={handleApplyOutfit}
              onApplyModification={handleApplyModification}
              onApplyEffect={handleApplyEffect}
              onApplyEnvironment={handleApplyEnvironment}
              onApplyAction={handleApplyAction}
              isLoading={isLoading}
              themeCategory={themes[activeTheme]}
            />
          </div>
        )}
      </main>
    </div>
  );
};

export default App;