import React from 'react';

interface EffectsPanelProps {
  onApplyEffect: (prompt: string) => void;
  isLoading: boolean;
}

const EffectsPanel: React.FC<EffectsPanelProps> = ({ onApplyEffect, isLoading }) => {
  const presets = [
    { name: 'Holographic Glitch', prompt: 'Apply a subtle holographic glitch effect, with faint scan lines and chromatic aberration.', imageUrl: 'data:image/png;base64,' },
    { name: 'Rainy Night City', prompt: 'Apply a dark, rainy night effect with realistic water droplets and reflections.', imageUrl: 'data:image/png;base64,' },
    { name: 'Worn-out VHS', prompt: 'Give the image a worn-out, retro VHS tape look with grain and slight color bleeding.', imageUrl: 'data:image/png;base64,' },
    { name: 'Infrared Vision', prompt: 'Apply a thermal, infrared vision effect to the image.', imageUrl: 'data:image/png;base64,' },
    { name: 'Data Stream', prompt: 'Overlay a subtle, flowing green data stream and matrix-style code.', imageUrl: 'data:image/png;base64,' },
    { name: 'Pixel Glitch', prompt: 'Apply a heavy pixelation and block glitch effect.', imageUrl: 'data:image/png;base64,' },
    { name: 'Security Cam', prompt: 'Give the image a low-fi, distorted security camera view with a timestamp overlay.', imageUrl: 'data:image/png;base64,' },
  ];

  return (
    <div className="grid grid-cols-3 sm:grid-cols-4 gap-2 p-1 animate-fade-in">
      {presets.map(preset => (
        <button
          key={preset.name}
          onClick={() => onApplyEffect(preset.prompt)}
          disabled={isLoading}
          className="relative group aspect-square border border-[var(--cyber-border)] hover:border-[var(--cyber-border-active)] transition-all duration-200 focus:outline-none focus:border-[var(--cyber-cyan)] disabled:opacity-50 disabled:cursor-not-allowed"
          aria-label={`Apply ${preset.name}`}
        >
          <img src={preset.imageUrl} alt={preset.name} className="w-full h-full object-cover" />
          <div className="absolute inset-0 bg-black/60 group-hover:bg-black/40 transition-all duration-200"></div>
          <div className="absolute bottom-0 left-0 p-2">
             <h4 className="text-white font-semibold text-xs uppercase tracking-wider text-left">{preset.name}</h4>
          </div>
          <div className="absolute top-1 right-1 h-3 w-3 border-2 border-[var(--cyber-cyan)] bg-[var(--cyber-bg)] opacity-0 group-hover:opacity-100 transition-opacity"></div>
        </button>
      ))}
    </div>
  );
};

export default EffectsPanel;
